/**
 * Assignment Repository
 * Part of the Data Access Layer
 * Handles API calls for assignment and submission operations
 */

import { apiClient, type ApiResponse } from '../../api/apiClient'
import type {
  SubmitAssignmentResponse,
  SubmissionListResponse,
  SubmissionHistoryResponse,
  AssignmentDetailResponse
} from '../../../business/models/assignment/types'

const API_BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8000/api/v1'

/**
 * Submit an assignment with file upload
 * Uses multipart/form-data instead of JSON
 */
export async function submitAssignment(
  assignmentId: number,
  studentId: number,
  file: File
): Promise<ApiResponse<SubmitAssignmentResponse>> {
  try {
    // Create FormData for file upload
    const formData = new FormData()
    formData.append('assignment_id', assignmentId.toString())
    formData.append('student_id', studentId.toString())
    formData.append('file', file)

    // Make request with fetch directly (apiClient doesn't support FormData)
    const response = await fetch(`${API_BASE_URL}/submissions`, {
      method: 'POST',
      body: formData
      // Don't set Content-Type header - browser will set it with boundary
    })

    const data = await response.json()

    if (!response.ok) {
      let errorMessage = 'Failed to submit assignment'

      if (data.detail) {
        errorMessage = data.detail
      } else if (data.message) {
        errorMessage = data.message
      } else if (typeof data === 'string') {
        errorMessage = data
      }

      return {
        error: errorMessage,
        status: response.status
      }
    }

    // Convert snake_case to camelCase
    const responseData: SubmitAssignmentResponse = {
      success: data.success,
      message: data.message,
      submission: data.submission ? {
        id: data.submission.id,
        assignmentId: data.submission.assignment_id,
        studentId: data.submission.student_id,
        fileName: data.submission.file_name,
        fileSize: data.submission.file_size,
        submissionNumber: data.submission.submission_number,
        submittedAt: new Date(data.submission.submitted_at),
        isLatest: data.submission.is_latest
      } : undefined
    }

    return {
      data: responseData,
      status: response.status
    }
  } catch (error) {
    return {
      error: error instanceof Error ? error.message : 'Failed to submit assignment',
      status: 0
    }
  }
}

/**
 * Get submission history for a specific student and assignment
 */
export async function getSubmissionHistory(
  assignmentId: number,
  studentId: number
): Promise<ApiResponse<SubmissionHistoryResponse>> {
  const response = await apiClient.get<SubmissionHistoryResponse>(
    `/submissions/history/${assignmentId}/${studentId}`
  )

  if (response.data) {
    // Convert snake_case to camelCase and date strings to Date objects
    response.data = {
      ...response.data,
      submissions: response.data.submissions.map((sub: any) => ({
        id: sub.id,
        assignmentId: sub.assignment_id,
        studentId: sub.student_id,
        fileName: sub.file_name,
        fileSize: sub.file_size,
        submissionNumber: sub.submission_number,
        submittedAt: new Date(sub.submitted_at),
        isLatest: sub.is_latest
      }))
    }
  }

  return response
}

/**
 * Get all submissions by a student
 */
export async function getStudentSubmissions(
  studentId: number,
  latestOnly: boolean = true
): Promise<ApiResponse<SubmissionListResponse>> {
  const response = await apiClient.get<SubmissionListResponse>(
    `/submissions/student/${studentId}?latest_only=${latestOnly}`
  )

  if (response.data) {
    // Convert snake_case to camelCase
    response.data = {
      ...response.data,
      submissions: response.data.submissions.map((sub: any) => ({
        id: sub.id,
        assignmentId: sub.assignment_id,
        studentId: sub.student_id,
        fileName: sub.file_name,
        fileSize: sub.file_size,
        submissionNumber: sub.submission_number,
        submittedAt: new Date(sub.submitted_at),
        isLatest: sub.is_latest,
        assignmentName: sub.assignment_name
      }))
    }
  }

  return response
}

/**
 * Get all submissions for an assignment (typically for teachers)
 */
export async function getAssignmentSubmissions(
  assignmentId: number,
  latestOnly: boolean = true
): Promise<ApiResponse<SubmissionListResponse>> {
  const response = await apiClient.get<SubmissionListResponse>(
    `/submissions/assignment/${assignmentId}?latest_only=${latestOnly}`
  )

  if (response.data) {
    // Convert snake_case to camelCase
    response.data = {
      ...response.data,
      submissions: response.data.submissions.map((sub: any) => ({
        id: sub.id,
        assignmentId: sub.assignment_id,
        studentId: sub.student_id,
        fileName: sub.file_name,
        fileSize: sub.file_size,
        submissionNumber: sub.submission_number,
        submittedAt: new Date(sub.submitted_at),
        isLatest: sub.is_latest,
        studentName: sub.student_name
      }))
    }
  }

  return response
}

/**
 * Get assignment details by ID
 * Note: Uses the existing classes endpoint
 */
export async function getAssignmentById(
  assignmentId: number,
  userId: number
): Promise<ApiResponse<AssignmentDetailResponse>> {
  const response = await apiClient.get<AssignmentDetailResponse>(
    `/assignments/${assignmentId}?user_id=${userId}`
  )

  if (response.data) {
    // Convert snake_case to camelCase
    // The API returns the assignment object directly inside the response wrapper
    // We need to ensure the types match
    const assignmentData = response.data.assignment as any

    response.data = {
      ...response.data,
      assignment: {
        id: assignmentData.id,
        classId: assignmentData.class_id,
        className: assignmentData.class_name,
        title: assignmentData.title,
        description: assignmentData.description,
        programmingLanguage: assignmentData.programming_language,
        deadline: new Date(assignmentData.deadline),
        allowResubmission: assignmentData.allow_resubmission,
        isActive: assignmentData.is_active,
        createdAt: new Date(assignmentData.created_at)
      }
    }
  }

  return response
}

/**
 * Delete Class Modal Component
 * Part of the Presentation Layer - Form Components
 */

import * as React from 'react'
import { cn } from '@/shared/utils/cn'
import { Button } from '@/presentation/components/ui/Button'
import { AlertTriangle, X } from 'lucide-react'

interface DeleteClassModalProps {
  isOpen: boolean
  onClose: () => void
  onConfirm: () => void
  className?: string
  isDeleting?: boolean
}

export function DeleteClassModal({
  isOpen,
  onClose,
  onConfirm,
  className,
  isDeleting = false
}: DeleteClassModalProps) {
  // Close on escape key
  React.useEffect(() => {
    function handleEscape(event: KeyboardEvent) {
      if (event.key === 'Escape' && !isDeleting) {
        onClose()
      }
    }

    if (isOpen) {
      document.addEventListener('keydown', handleEscape)
      // Prevent body scroll when modal is open
      document.body.style.overflow = 'hidden'
    }

    return () => {
      document.removeEventListener('keydown', handleEscape)
      document.body.style.overflow = 'unset'
    }
  }, [isOpen, onClose, isDeleting])

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black/60 backdrop-blur-sm"
        onClick={!isDeleting ? onClose : undefined}
      />

      {/* Modal */}
      <div
        className={cn(
          'relative w-full max-w-md mx-4 p-6',
          'rounded-xl border border-white/10 bg-slate-900/95 backdrop-blur-sm',
          'shadow-xl shadow-black/20',
          'animate-in fade-in-0 zoom-in-95 duration-200',
          className
        )}
        role="dialog"
        aria-modal="true"
        aria-labelledby="delete-modal-title"
      >
        {/* Close button */}
        <button
          onClick={onClose}
          disabled={isDeleting}
          className={cn(
            'absolute top-4 right-4 p-1 rounded-lg',
            'text-gray-400 hover:text-white hover:bg-white/10',
            'transition-colors duration-200',
            'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-purple-500',
            'disabled:opacity-50 disabled:cursor-not-allowed'
          )}
        >
          <X className="w-5 h-5" />
        </button>

        {/* Warning icon */}
        <div className="flex justify-center mb-4">
          <div className="w-16 h-16 rounded-full bg-red-500/20 flex items-center justify-center">
            <AlertTriangle className="w-8 h-8 text-red-400" />
          </div>
        </div>

        {/* Title */}
        <h2
          id="delete-modal-title"
          className="text-xl font-semibold text-white text-center mb-2"
        >
          Delete Class
        </h2>

        {/* Description */}
        <p className="text-gray-400 text-center mb-6">
          Are you sure you want to delete this class? This action cannot be undone.
          All assignments and student enrollments will be removed.
        </p>

        {/* Actions */}
        <div className="flex gap-3">
          <button
            onClick={onClose}
            disabled={isDeleting}
            className={cn(
              'flex-1 px-4 py-3 rounded-xl text-sm font-semibold',
              'border border-white/20 text-white',
              'hover:bg-white/10 transition-colors duration-200',
              'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-purple-500',
              'disabled:opacity-50 disabled:cursor-not-allowed'
            )}
          >
            Cancel
          </button>
          <button
            onClick={onConfirm}
            disabled={isDeleting}
            className={cn(
              'flex-1 px-4 py-3 rounded-xl text-sm font-semibold',
              'bg-red-500 text-white',
              'hover:bg-red-600 transition-colors duration-200',
              'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-red-500',
              'disabled:opacity-50 disabled:cursor-not-allowed'
            )}
          >
            {isDeleting ? 'Deleting...' : 'Delete Class'}
          </button>
        </div>
      </div>
    </div>
  )
}

/**
 * Leave Class Modal Component
 * Part of the Presentation Layer - Forms
 * Confirmation modal for students to leave/unenroll from a class
 */

import { useState } from 'react'
import { X, LogOut, AlertTriangle, RefreshCw } from 'lucide-react'
import { Button } from '@/presentation/components/ui/Button'
import { leaveClass } from '@/business/services/dashboard/studentDashboardService'

interface LeaveClassModalProps {
  isOpen: boolean
  onClose: () => void
  onSuccess: () => void
  studentId: number
  classId: number
  className: string
}

export function LeaveClassModal({
  isOpen,
  onClose,
  onSuccess,
  studentId,
  classId,
  className
}: LeaveClassModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [error, setError] = useState<string | null>(null)

  const handleConfirm = async () => {
    setError(null)
    setIsSubmitting(true)

    try {
      const response = await leaveClass(studentId, classId)

      if (!response.success) {
        setError(response.message || 'Failed to leave class')
        return
      }

      onSuccess()
      onClose()
    } catch (err) {
      setError('Failed to leave class. Please try again.')
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black/60 backdrop-blur-sm"
        onClick={onClose}
      />

      {/* Modal */}
      <div className="relative w-full max-w-md bg-slate-900/95 backdrop-blur-xl rounded-2xl border border-white/10 shadow-2xl z-10">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/10">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-red-500/20">
              <AlertTriangle className="w-5 h-5 text-red-400" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-white">Leave Class</h2>
              <p className="text-sm text-gray-400 mt-0.5">
                This action cannot be undone
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-2 rounded-lg hover:bg-white/10 transition-colors text-gray-400 hover:text-white"
            aria-label="Close"
            disabled={isSubmitting}
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-4">
          {/* Error Message */}
          {error && (
            <div className="p-3 rounded-lg bg-red-500/10 border border-red-500/20">
              <p className="text-sm text-red-400">{error}</p>
            </div>
          )}

          {/* Warning Message */}
          <div className="p-4 rounded-lg bg-yellow-500/10 border border-yellow-500/20">
            <p className="text-sm text-yellow-200">
              Are you sure you want to leave{' '}
              <span className="font-semibold text-white">{className}</span>?
            </p>
            <ul className="mt-3 space-y-2 text-xs text-gray-400">
              <li className="flex items-start gap-2">
                <span className="text-yellow-500 mt-0.5">ΓÇó</span>
                <span>You will lose access to all class assignments and materials</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-yellow-500 mt-0.5">ΓÇó</span>
                <span>Your submissions will still be visible to your teacher</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-yellow-500 mt-0.5">ΓÇó</span>
                <span>You will need a new class code to rejoin later</span>
              </li>
            </ul>
          </div>

          {/* Actions */}
          <div className="flex gap-3 pt-2">
            <Button
              type="button"
              onClick={onClose}
              disabled={isSubmitting}
              className="flex-1 bg-white/10 hover:bg-white/20 text-white border border-white/20"
            >
              <X className="w-4 h-4 mr-2" />
              Cancel
            </Button>
            <Button
              type="button"
              onClick={handleConfirm}
              disabled={isSubmitting}
              className="flex-1 bg-red-600 hover:bg-red-700 text-white"
            >
              {isSubmitting ? (
                <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
              ) : (
                <LogOut className="w-4 h-4 mr-2" />
              )}
              Leave Class
            </Button>
          </div>
        </div>
      </div>
    </div>
  )
}

/**
 * Remove Student Modal Component
 * Part of the Presentation Layer - Forms
 * Confirmation modal for teachers to remove a student from a class
 */

import { useState } from 'react'
import { X, Trash2, AlertTriangle, RefreshCw } from 'lucide-react'
import { Button } from '@/presentation/components/ui/Button'
import { removeStudent } from '@/business/services/class/classService'

interface RemoveStudentModalProps {
    isOpen: boolean
    onClose: () => void
    onSuccess: () => void
    classId: number
    teacherId: number
    studentId: number
    studentName: string
}

export function RemoveStudentModal({
    isOpen,
    onClose,
    onSuccess,
    classId,
    teacherId,
    studentId,
    studentName
}: RemoveStudentModalProps) {
    const [isSubmitting, setIsSubmitting] = useState(false)
    const [error, setError] = useState<string | null>(null)

    const handleConfirm = async () => {
        setError(null)
        setIsSubmitting(true)

        try {
            await removeStudent(classId, studentId, teacherId)
            onSuccess()
            onClose()
        } catch (err) {
            setError('Failed to remove student. Please try again.')
        } finally {
            setIsSubmitting(false)
        }
    }

    if (!isOpen) return null

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            {/* Backdrop */}
            <div
                className="absolute inset-0 bg-black/60 backdrop-blur-sm"
                onClick={onClose}
            />

            {/* Modal */}
            <div className="relative w-full max-w-md bg-slate-900/95 backdrop-blur-xl rounded-2xl border border-white/10 shadow-2xl z-10">
                {/* Header */}
                <div className="flex items-center justify-between p-6 border-b border-white/10">
                    <div className="flex items-center gap-3">
                        <div className="p-2 rounded-lg bg-red-500/20">
                            <AlertTriangle className="w-5 h-5 text-red-400" />
                        </div>
                        <div>
                            <h2 className="text-xl font-bold text-white">Remove Student</h2>
                            <p className="text-sm text-gray-400 mt-0.5">
                                This action cannot be undone
                            </p>
                        </div>
                    </div>
                    <button
                        onClick={onClose}
                        className="p-2 rounded-lg hover:bg-white/10 transition-colors text-gray-400 hover:text-white"
                        aria-label="Close"
                        disabled={isSubmitting}
                    >
                        <X className="w-5 h-5" />
                    </button>
                </div>

                {/* Content */}
                <div className="p-6 space-y-4">
                    {/* Error Message */}
                    {error && (
                        <div className="p-3 rounded-lg bg-red-500/10 border border-red-500/20">
                            <p className="text-sm text-red-400">{error}</p>
                        </div>
                    )}

                    {/* Warning Message */}
                    <div className="p-4 rounded-lg bg-yellow-500/10 border border-yellow-500/20">
                        <p className="text-sm text-yellow-200">
                            Are you sure you want to remove{' '}
                            <span className="font-semibold text-white">{studentName}</span> from this class?
                        </p>
                        <ul className="mt-3 space-y-2 text-xs text-gray-400">
                            <li className="flex items-start gap-2">
                                <span className="text-yellow-500 mt-0.5">ΓÇó</span>
                                <span>The student will lose access to all class assignments</span>
                            </li>
                            <li className="flex items-start gap-2">
                                <span className="text-yellow-500 mt-0.5">ΓÇó</span>
                                <span>Their submissions will be preserved but they cannot submit new work</span>
                            </li>
                        </ul>
                    </div>

                    {/* Actions */}
                    <div className="flex gap-3 pt-2">
                        <Button
                            type="button"
                            onClick={onClose}
                            disabled={isSubmitting}
                            className="flex-1 bg-white/10 hover:bg-white/20 text-white border border-white/20"
                        >
                            <X className="w-4 h-4 mr-2" />
                            Cancel
                        </Button>
                        <Button
                            type="button"
                            onClick={handleConfirm}
                            disabled={isSubmitting}
                            className="flex-1 bg-red-600 hover:bg-red-700 text-white"
                        >
                            {isSubmitting ? (
                                <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                            ) : (
                                <Trash2 className="w-4 h-4 mr-2" />
                            )}
                            Remove Student
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    )
}

/**
 * Toast Component
 * Part of the Presentation Layer - UI Components
 * Displays temporary notification messages
 */

import * as React from 'react'
import { cn } from '@/shared/utils/cn'
import { X, CheckCircle, AlertCircle, Info } from 'lucide-react'

export type ToastVariant = 'success' | 'error' | 'info'

export interface ToastProps {
  id: string
  message: string
  variant?: ToastVariant
  duration?: number
  onDismiss: (id: string) => void
}

const variantStyles: Record<ToastVariant, { bg: string; icon: React.ReactNode }> = {
  success: {
    bg: 'bg-green-500/20 border-green-500/30',
    icon: <CheckCircle className="w-5 h-5 text-green-400" />
  },
  error: {
    bg: 'bg-red-500/20 border-red-500/30',
    icon: <AlertCircle className="w-5 h-5 text-red-400" />
  },
  info: {
    bg: 'bg-blue-500/20 border-blue-500/30',
    icon: <Info className="w-5 h-5 text-blue-400" />
  }
}

export function Toast({ id, message, variant = 'success', duration = 4000, onDismiss }: ToastProps) {
  const [isVisible, setIsVisible] = React.useState(false)
  const [isLeaving, setIsLeaving] = React.useState(false)

  React.useEffect(() => {
    // Trigger enter animation
    const enterTimer = setTimeout(() => setIsVisible(true), 10)

    // Auto-dismiss after duration
    const dismissTimer = setTimeout(() => {
      handleDismiss()
    }, duration)

    return () => {
      clearTimeout(enterTimer)
      clearTimeout(dismissTimer)
    }
  }, [duration])

  const handleDismiss = () => {
    setIsLeaving(true)
    setTimeout(() => {
      onDismiss(id)
    }, 300) // Match animation duration
  }

  const styles = variantStyles[variant]

  return (
    <div
      className={cn(
        'flex items-center gap-3 px-4 py-3 rounded-lg border backdrop-blur-sm shadow-lg',
        'transition-all duration-300 ease-out',
        styles.bg,
        isVisible && !isLeaving
          ? 'translate-x-0 opacity-100'
          : 'translate-x-full opacity-0'
      )}
      role="alert"
    >
      {styles.icon}
      <p className="text-sm text-white font-medium flex-1">{message}</p>
      <button
        onClick={handleDismiss}
        className="p-1 rounded hover:bg-white/10 transition-colors"
        aria-label="Dismiss"
      >
        <X className="w-4 h-4 text-gray-400" />
      </button>
    </div>
  )
}

export interface ToastContainerProps {
  toasts: Array<{
    id: string
    message: string
    variant?: ToastVariant
  }>
  onDismiss: (id: string) => void
}

export function ToastContainer({ toasts, onDismiss }: ToastContainerProps) {
  if (toasts.length === 0) return null

  return (
    <div className="fixed top-4 right-4 z-50 flex flex-col gap-2 max-w-sm">
      {toasts.map((toast) => (
        <Toast
          key={toast.id}
          id={toast.id}
          message={toast.message}
          variant={toast.variant}
          onDismiss={onDismiss}
        />
      ))}
    </div>
  )
}

/**
 * Toast Context
 * Part of the Shared Layer - Context
 * Provides toast notification functionality across the app
 */

import * as React from 'react'
import { ToastContainer } from '@/presentation/components/ui/Toast'
import type { ToastVariant } from '@/presentation/components/ui/Toast'

interface Toast {
  id: string
  message: string
  variant?: ToastVariant
}

interface ToastContextType {
  showToast: (message: string, variant?: ToastVariant) => void
  dismissToast: (id: string) => void
}

const ToastContext = React.createContext<ToastContextType | undefined>(undefined)

export function ToastProvider({ children }: { children: React.ReactNode }) {
  const [toasts, setToasts] = React.useState<Toast[]>([])

  const showToast = React.useCallback((message: string, variant: ToastVariant = 'success') => {
    const id = `toast-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
    setToasts((prev) => [...prev, { id, message, variant }])
  }, [])

  const dismissToast = React.useCallback((id: string) => {
    setToasts((prev) => prev.filter((toast) => toast.id !== id))
  }, [])

  return (
    <ToastContext.Provider value={{ showToast, dismissToast }}>
      {children}
      <ToastContainer toasts={toasts} onDismiss={dismissToast} />
    </ToastContext.Provider>
  )
}

export function useToast() {
  const context = React.useContext(ToastContext)
  if (context === undefined) {
    throw new Error('useToast must be used within a ToastProvider')
  }
  return context
}
